import React, { Component } from "react";
import $ from "jquery";
import "./home.css";
import { connect } from 'react-redux';
import { compose } from 'recompose';
import {
	BrowserRouter as Router,
	Switch,
	Route,
	Link,
	useRouteMatch,
	withRouter,
	useParams
} from "react-router-dom";
import { withFirebase } from '../components/Firebase/context'
import { Player, ControlBar } from 'video-react';
import { PrismCode } from 'react-prism';
import { Button } from 'reactstrap';


const tit = () => (
	<div>
		<Hohoho />
	</div>
);

const sources = {
	movie: ''
}

class Home extends Component {
	constructor(props) {
		super(props);
		this.state = {
			source: null,
			url: null,
			error: null,
		}
		this.play = this.play.bind(this);
		this.pause = this.pause.bind(this);
		this.load = this.load.bind(this);
		this.changeCurrentTime = this.changeCurrentTime.bind(this);
		this.seek = this.seek.bind(this);
		this.changePlaybackRateRate = this.changePlaybackRateRate.bind(this);
		this.changeVolume = this.changeVolume.bind(this);
		this.setMuted = this.setMuted.bind(this);
	}

	setMuted(muted) {
		return () => {
			this.player.muted = muted;
		};
	}

	handleStateChange(state) {

		this.setState({
			player: state
		});
	}

	play() {
		this.player.play();
	}

	pause() {
		this.player.pause();
	}

	load() {
		this.player.load();
	}

	changeCurrentTime(seconds) {
		return () => {
			const { player } = this.player.getState();
			this.player.seek(player.currentTime + seconds);
		};
	}

	seek(seconds) {
		return () => {
			this.player.seek(seconds);
		};
	}

	changePlaybackRateRate(steps) {
		return () => {
			const { player } = this.player.getState();
			this.player.playbackRate = player.playbackRate + steps;
		};
	}

	changeVolume(steps) {
		return () => {
			const { player } = this.player.getState();
			this.player.volume = player.volume + steps;
		};
	}

	changeSource(name) {
		return () => {
			this.setState({
				source: sources[name]
			});
			this.player.load();
		};
	}



	async componentDidMount() {
		const response = await this.props.firebase.doGrabFile();
		this.setState({ source: response });
		console.log(this.state.source)
		this.player.subscribeToStateChange(this.handleStateChange.bind(this));
	}



	handleKeyUp() {
		$(".post-comment").keyup(data => {
			if (data.target.value !== "") {
				$(data.target.nextElementSibling).removeClass("disabled");
			} else {
				$(data.target.nextElementSibling).addClass("disabled");
			}
		});
	}
	generatePosts() {
		console.log(this.props.user.username)
		let posts = [];

		// Outer loop to create parent
		for (let i = 0; i < 3; i++) {
			//Create the parent and add the children
			posts.push(
				<div class="post animated bounceInLeft delay-1s">
					<div class="post-header">
						<div class="post-author">
							<a href="#">
								<img
									src="https://www.speakingtigerbooks.com/wp-content/uploads/2016/09/facebook-default-no-profile-pic.jpg"
									alt="Author Picture"
									srcset=""
								/>
							</a>
							<p class="post-name">Username {i}</p>
						</div>
						<div class="post-settings">
							<a href="#">
								<img class="pure-img" src="imgs/report.svg" alt="" srcset="" />
							</a>
						</div>
					</div>
					<div class="post-content">
						<Player
							ref={player => {
								this.player = player;
							}}
							autoPlay
						>
							<source src={this.state.source} />
							<ControlBar autoHide={false} />
							</Player>
					</div>
					<div className="py-3">
          <Button onClick={this.play} className="mr-3">
            play()
          </Button>
          <Button onClick={this.pause} className="mr-3">
            pause()
          </Button>
          <Button onClick={this.load} className="mr-3">
            load()
          </Button>
        </div>
        <div className="pb-3">
          <Button onClick={this.changeCurrentTime(10)} className="mr-3">
            currentTime += 10
          </Button>
          <Button onClick={this.changeCurrentTime(-10)} className="mr-3">
            currentTime -= 10
          </Button>
          <Button onClick={this.seek(50)} className="mr-3">
            currentTime = 50
          </Button>
        </div>
        <div className="pb-3">
          <Button onClick={this.changePlaybackRateRate(1)} className="mr-3">
            playbackRate++
          </Button>
          <Button onClick={this.changePlaybackRateRate(-1)} className="mr-3">
            playbackRate--
          </Button>
          <Button onClick={this.changePlaybackRateRate(0.1)} className="mr-3">
            playbackRate+=0.1
          </Button>
          <Button onClick={this.changePlaybackRateRate(-0.1)} className="mr-3">
            playbackRate-=0.1
