import React, { Component } from "react";
import $ from "jquery";
import "./header.css";
import {
	BrowserRouter as Router,
	Switch,
	Route,
	Link,
	useRouteMatch,
	useParams
} from "react-router-dom";
import { connect } from 'react-redux';
import fire from '../components/Firebase/fire'

class Header extends Component {
	constructor(props) {
		super(props);
		this.state={
			selectedFile: null,
		}
    }

	toggleDropDown = event => {
		event.preventDefault();
		$("#dropdownMenu").toggle("show");
	};

	onSubmit = event => {

		console.log(event.target.files[0])
		/*(let files = event.target.files
		let reader = new FileReader();
		reader.readAsDataURL(files[0])
		reader.onload=(e)=>{
			this.props.firebase.doSubmitFile(e.target.result)
		}
		var file_data = $('#uploadForm').prop('files')[0];
*/	
		let files = event.target.files
		this.props.firebase
            .doSubmitFile(files)
         .then(() => {
                console.log(event.target.files[0])
            })
            .catch(error => {
               console.log('error')
            });

        event.preventDefault();

		/*
		var timestamp = Number(new Date());
		var storageRef = fire.storage().ref(timestamp.toString())

		console.log(storageRef)
		 var file_data = $('#uploadForm').prop('files')[0];
		 storageRef.put(file_data); */
	};

	onClickHandler = () => {

		const data = new FormData() 
		data.append('file', this.state.selectedFile)
		console.log(data)
		this.props.firebase
            .doSubmitFile(data)
         .then(() => {
                console.log(data)
            })
            .catch(error => {
               console.log('error')
            });
	}


	onChangeHandler=event=>{
		this.setState({
		  selectedFile: event.target.files[0],
		  loaded: 0,
		})
	  }

	render(){
	return (
		<div class="wide">
			<nav class="">
				<div class="nav-col-1">
					<div class="header-cam dropdown-btn">
						<a href="#" onClick={this.toggleDropDown}>
							<i class="las la-video"></i>
						</a>
						<div id="dropdownMenu" class="dropdown-content">
							<form id="uploadForm" method= "post">
								<input type="file" name="file" onChange={this.onChangeHandler} />
								<p>Drag files here?</p>
								<button type="button"
								class="btn btn-success btn-block"
								onClick={this.onClickHandler}>Upload</button>
							</form>
						</div>
					</div>
				</div>
				<div class="nav-col-2">
					<div class="nav-text">
						<Link to="/home">FlipReset</Link>
					</div>
				</div>
				<div class="nav-col-3">
					<div class="header-messages">
						<Link to="/messages">
							<i class="lab la-telegram-plane"></i>
						</Link>
						<Link to="/profile">
							<i class="las la-user"></i>
						</Link>
					</div>
				</div>
			</nav>
		</div>
	);
	}
}

const mapStateToProps = (state) => {
    const { user} = state;
    return {
      user,
    }
  }


  export default connect(mapStateToProps, null)(Header);
