import React, { Component } from "react";
import $ from "jquery";
import "./header.css";
import {
	BrowserRouter as Router,
	Switch,
	Route,
	Link,
	useRouteMatch,
	useParams
} from "react-router-dom";
import { connect } from 'react-redux';
import fire from '../components/Firebase/fire'

class Header extends Component {
	constructor(props) {
        super(props);
    }

	toggleDropDown = event => {
		event.preventDefault();
		$("#dropdownMenu").toggle("show");
	};

	onSubmit = event => {


		/*(let files = event.target.files
		let reader = new FileReader();
		reader.readAsDataURL(files[0])
		reader.onload=(e)=>{
			this.props.firebase.doSubmitFile(e.target.result)
		}
		var file_data = $('#uploadForm').prop('files')[0];
*/	
		let files = event.target.files
		this.props.firebase
            .doSubmitFile(files)
         .then(() => {
                console.log(event.target.result)
            })
            .catch(error => {
               console.log('error')
            });

        event.preventDefault();

		/*
		var timestamp = Number(new Date());
		var storageRef = fire.storage().ref(timestamp.toString())

		console.log(storageRef)
		 var file_data = $('#uploadForm').prop('files')[0];
		 storageRef.put(file_data); */
    };

	render(){
	return (
		<div class="wide">
			<nav class="">
				<div class="nav-col-1">
					<div class="header-cam dropdown-btn">
						<a href="#" onClick={this.toggleDropDown}>
							<i class="las la-video"></i>
						</a>
						<div id="dropdownMenu" class="dropdown-content">
							<form id="uploadForm" method= "post">
								<input type="file" multiple />
								<p>Drag files here?</p>
								<button type="submit" onSubmi={this.onSubmit}>Upload</button>
							</form>
						</div>
					</div>
				</div>
				<div class="nav-col-2">
					<div class="nav-text">
						<Link to="/home">FlipReset</Link>
					</div>
				</div>
				<div class="nav-col-3">
					<div class="header-messages">
						<Link to="/messages">
							<i class="lab la-telegram-plane"></i>
						</Link>
						<Link to="/profile">
							<i class="las la-user"></i>
						</Link>
					</div>
				</div>
			</nav>
		</div>
	);
	}
}

const mapStateToProps = (state) => {
    const { user} = state;
    return {
      user,
    }
  }


  export default connect(mapStateToProps, null)(Header);
